import { useState } from "react";
import Button from "../Button/Button";
import styles from "./UserInput.module.css";
const UserInput = (props) => {
  const [username, setUsername] = useState("");
  const [age, setAge] = useState("");

  const usernameInputHandler = (e) => {
    setUsername(e.target.value);
  };
  const ageInputHandler = (e) => {
    setAge(e.target.value);
  };

  const submitHandler = (e) => {
    const [errorMsg, setErrorMsg] = useState("");
    e.preventDefault();
    if (!username || !age) {
      setErrorMsg("Please enter your Name and Age");
      props.content(errorMsg);
    } else if (age < 1) {
      setErrorMsg("Invalid age");
      props.content(errorMsg);
    }

    if (username && age > 1) {
      const user = { username, age };
      props.addToList(user);

      setUsername("");
      setAge("");
    }
  };
  return (
    <form onSubmit={submitHandler} className={styles.input}>
      <label htmlFor="username">Username</label>
      <input
        type="text"
        value={username}
        onChange={usernameInputHandler}
        id="username"
      />
      <label htmlFor="number">Age</label>
      <input type="number" value={age} onChange={ageInputHandler} id="age" />
      <Button />
    </form>
  );
};
export default UserInput;
